// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "./TruthForgeToken.sol";
import "./ValidationPool.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title PoolFactory
 * @dev Factory for creating validation pools from news inputs (e.g., hashes of X posts/RSS). Aligns with TruthForge spec: User/oracle-driven creation, micro-pool consensus to skip ZK for easily verifiable items, time-bound closures.
 * Features:
 * - Fee in $VERIFY to create pool (anti-spam, funds treasury).
 * - Micro-pool stub for sentiment consensus (oracle/owner call; decentralize later).
 * - Auto-closure via timelock or easy verification flag.
 * - Events for dApp UI integration (e.g., Next.js feed).
 * - Edge for unstable regions: Bitchat offline push—dApp queues _newsHash as simple object (e.g., {hash: bytes32, metadata: string}), syncs to chain via UI call when connected. Factory accepts hashed objects.
 * - Oracle stub for auto-creation from feeds (Chainlink for RSS/X data).
 */
contract PoolFactory is Ownable, ReentrancyGuard {
    VerifyToken public token;
    uint256 public poolCreationFee = 10 * 10**18; // 10 $VERIFY
    uint256 public microPoolDuration = 2 hours;
    uint256 public mainPoolDuration = 24 hours;
    uint256 public verifiableThreshold = 80; // % consensus to skip ZK/main phase

    address public oracleAddress; // For automated calls (set to Chainlink or DAO)

    mapping(bytes32 => address) public pools; // _newsHash -> ValidationPool address
    mapping(bytes32 => bool) public easilyVerifiable; // Flag to skip ZK if consensus high

    event PoolCreated(bytes32 indexed newsHash, address poolAddress, uint256 startTime, bool fromOracle);
    event MicroConsensusReached(bytes32 indexed newsHash, uint8 consensusPercent, bool skipZK);
    event PoolClosed(bytes32 indexed newsHash, address poolAddress, bool earlyClosure);

    constructor(VerifyToken _token, address _oracle) Ownable(msg.sender) {
        token = _token;
        oracleAddress = _oracle; // Initial oracle (updateable)
    }

    // User creation: Pay fee, submit _newsHash from feed
    function createPool(bytes32 _newsHash) external nonReentrant {
        require(pools[_newsHash] == address(0), "Pool exists");
        token.transferFrom(msg.sender, address(this), poolCreationFee);

        ValidationPool newPool = new ValidationPool(token, _newsHash, block.timestamp + mainPoolDuration);
        pools[_newsHash] = address(newPool);

        emit PoolCreated(_newsHash, address(newPool), block.timestamp, false);
    }

    // Oracle auto-creation: Trustless from feeds (e.g., Chainlink pushes high-impact _newsHash)
    function createPoolFromOracle(bytes32 _newsHash) external {
        require(msg.sender == oracleAddress, "Only oracle");
        require(pools[_newsHash] == address(0), "Pool exists");

        // Fee from treasury or waived for auto
        ValidationPool newPool = new ValidationPool(token, _newsHash, block.timestamp + mainPoolDuration);
        pools[_newsHash] = address(newPool);

        emit PoolCreated(_newsHash, address(newPool), block.timestamp, true);
    }

    // Micro-pool resolution: Consensus on sentiment/subject (oracle/owner stub; full votes in ValidationPool)
    function resolveMicroPool(bytes32 _newsHash, uint8 consensusPercent) external {
        require(msg.sender == oracleAddress || msg.sender == owner(), "Only oracle/owner"); // Decentralize later
        require(pools[_newsHash] != address(0), "Pool not exists");
        // Note: ValidationPool doesn't have startTime, using creation time estimation
        require(block.timestamp <= ValidationPool(pools[_newsHash]).endTime() - mainPoolDuration + microPoolDuration, "Micro-pool ended");

        if (consensusPercent >= verifiableThreshold) {
            easilyVerifiable[_newsHash] = true;
            // Set flag in ValidationPool and it will auto-close
            ValidationPool(pools[_newsHash]).setEasilyVerifiable(true);
        }

        emit MicroConsensusReached(_newsHash, consensusPercent, easilyVerifiable[_newsHash]);
    }

    // Close pool: Time-bound or early if verifiable (refunds/distributes in ValidationPool)
    function closePool(bytes32 _newsHash) public {
        ValidationPool pool = ValidationPool(pools[_newsHash]);
        require(address(pool) != address(0), "Pool not exists");
        require(block.timestamp >= pool.endTime() || easilyVerifiable[_newsHash] || msg.sender == owner(), "Pool active");

        // If owner is forcing closure, set easily verifiable flag first
        if (msg.sender == owner() && !easilyVerifiable[_newsHash] && block.timestamp < pool.endTime()) {
            pool.setEasilyVerifiable(true);
        } else {
            pool.closeAndDistribute(); // Handles refunds/rewards
        }

        emit PoolClosed(_newsHash, address(pool), block.timestamp < pool.endTime());
    }

    // Admin setters
    function setCreationFee(uint256 _newFee) external onlyOwner {
        poolCreationFee = _newFee;
    }

    function setDurations(uint256 _micro, uint256 _main) external onlyOwner {
        microPoolDuration = _micro;
        mainPoolDuration = _main;
    }

    function setThreshold(uint256 _newThreshold) external onlyOwner {
        verifiableThreshold = _newThreshold;
    }

    function setOracle(address _newOracle) external onlyOwner {
        oracleAddress = _newOracle;
    }

    // Withdraw fees to treasury
    function withdrawFees() external onlyOwner {
        token.transfer(owner(), token.balanceOf(address(this)));
    }
}

