// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;  // Updated to 0.8.26 as per your config

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title TruthForge Token ($VERIFY)
 * @dev ERC-20 token optimized for TruthForge protocol: validation rewards, staking with proximity weighting, and ZK integration.
 * Updates from original:
 * - Simplified for MVP: Removed vesting and public sale (move to separate contracts for modularity/grant focus).
 * - Added deflation: Burn 0.5% on transfers/stakes (configurable).
 * - Enhanced staking: Proximity-based multipliers (stub for degree proof), reward cap to prevent inflation.
 * - ZK rewards: Replaced owner-only with ZK proof stub (integrate ZKVerifier.sol later; for now, simple hash check).
 * - Reduced supply to 1B (original spec); added mint cap.
 * - Security: Timelock on emergencyWithdraw, stricter access.
 * - Protocol alignment: Rewards for pool validations, staking for consensus weights.
 * - Mixer prep: Burn on offload; separate Mixer.sol planned for anonymous withdrawals.
 */
contract VerifyToken is ERC20, ERC20Burnable, ERC20Pausable, Ownable, ReentrancyGuard {
    uint256 public constant TOTAL_SUPPLY_CAP = 100_000_000_000 * 10**18; // Capped at 100B per original spec
    uint256 public mintedSupply = 0; // Track to enforce cap

    // Staking config
    mapping(address => StakeInfo) public stakes;
    mapping(address => uint256) public stakingRewards;
    uint256 public stakingRewardRate = 100; // 1% annual (basis points)
    uint256 public minimumStakePeriod = 30 days;
    uint256 public totalStaked = 0;
    uint256 public deflationBurnRate = 50; // 0.5% burn on transfers/stakes (basis points)

    // ZK Verification rewards (protocol alignment: Reward pool validations)
    mapping(address => uint256) public verificationRewards;
    mapping(bytes32 => bool) public verifiedProofs;
    uint256 public verificationRewardAmount = 100 * 10**18; // 100 tokens per valid proof
    uint256 public maxRewardPool = TOTAL_SUPPLY_CAP / 10; // Cap rewards at 10% of supply
    uint256 public distributedRewards = 0;

    // Emergency timelock
    uint256 public constant EMERGENCY_TIMELOCK = 1 days;
    uint256 public emergencyWithdrawTimestamp = 0;

    struct StakeInfo {
        uint256 amount;
        uint256 startTime;
        uint256 lastRewardTime;
        bool active;
        uint8 proximityDegree; // 1-3 for weighting (stub for ZK proof)
    }

    // Events (kept core ones, added for protocol)
    event TokensStaked(address indexed staker, uint256 amount, uint8 degree);
    event TokensUnstaked(address indexed staker, uint256 amount, uint256 reward);
    event StakingRewardsClaimed(address indexed staker, uint256 reward);
    event ProofVerified(address indexed verifier, bytes32 indexed proofHash, uint256 reward);
    event BurnedOnTransfer(address indexed from, uint256 amount);

    constructor(address initialOwner) ERC20("Verify", "VERIFY") Ownable(initialOwner) {
        mintedSupply = TOTAL_SUPPLY_CAP;
        _mint(initialOwner, TOTAL_SUPPLY_CAP); // Mint cap to treasury
    }

    // Staking Functions (updated for proximity weighting)
    function stake(uint256 _amount, uint8 _degree) external {
        require(_amount > 0, "Amount must be positive");
        require(balanceOf(msg.sender) >= _amount, "Insufficient balance");
        require(_degree >= 1 && _degree <= 3, "Invalid proximity degree"); // Stub: ZK prove later

        StakeInfo storage userStake = stakes[msg.sender];

        if (userStake.active) {
            _claimStakingRewards(msg.sender);
            userStake.amount += _amount;
        } else {
            userStake.amount = _amount;
            userStake.active = true;
        }

        userStake.startTime = block.timestamp;
        userStake.lastRewardTime = block.timestamp;
        userStake.proximityDegree = _degree;
        totalStaked += _amount;

        // Burn deflation %
        uint256 burnAmount = (_amount * deflationBurnRate) / 10000;
        _burn(msg.sender, burnAmount);
        _transfer(msg.sender, address(this), _amount - burnAmount);

        emit TokensStaked(msg.sender, _amount, _degree);
        emit BurnedOnTransfer(msg.sender, burnAmount);
    }

    function unstake() external nonReentrant {
        StakeInfo storage userStake = stakes[msg.sender];
        require(userStake.active, "No active stake");
        require(block.timestamp >= userStake.startTime + minimumStakePeriod, "Minimum period not met");

        uint256 stakedAmount = userStake.amount;
        uint256 reward = calculateStakingReward(msg.sender);

        userStake.amount = 0;
        userStake.active = false;
        totalStaked -= stakedAmount;

        // Transfer the actual amount available in contract (after deflation burn)
        uint256 contractBalance = balanceOf(address(this));
        uint256 transferAmount = contractBalance < stakedAmount ? contractBalance : stakedAmount;
        _transfer(address(this), msg.sender, transferAmount);

        if (reward > 0) {
            require(distributedRewards + reward <= maxRewardPool, "Reward pool exhausted");
            _mint(msg.sender, reward);
            distributedRewards += reward;
            stakingRewards[msg.sender] += reward;
        }

        emit TokensUnstaked(msg.sender, stakedAmount, reward);
    }

    function claimStakingRewards() external {
        _claimStakingRewards(msg.sender);
    }

    function _claimStakingRewards(address _staker) internal {
        uint256 reward = calculateStakingReward(_staker);
        if (reward > 0) {
            require(distributedRewards + reward <= maxRewardPool, "Reward pool exhausted");
            stakes[_staker].lastRewardTime = block.timestamp;
            _mint(_staker, reward);
            distributedRewards += reward;
            stakingRewards[_staker] += reward;
            emit StakingRewardsClaimed(_staker, reward);
        }
    }

    function calculateStakingReward(address _staker) public view returns (uint256) {
        StakeInfo memory userStake = stakes[_staker];
        if (!userStake.active || userStake.amount == 0) {
            return 0;
        }

        uint256 timeStaked = block.timestamp - userStake.lastRewardTime;
        uint256 baseAnnualReward = (userStake.amount * stakingRewardRate) / 10000;
        uint256 degreeMultiplier = 4 - userStake.proximityDegree; // Higher for closer (1=3x, 3=1x)
        uint256 annualReward = baseAnnualReward * degreeMultiplier;
        return (annualReward * timeStaked) / 365 days;
    }

    // ZK Verification Functions (updated for protocol: Stub for ZK, decentralized via proof hash)
    function verifyProofAndReward(bytes32 _proofHash, address _verifier) external {
        require(!verifiedProofs[_proofHash], "Proof already verified");
        require(_verifier != address(0), "Invalid verifier");
        // Stub: In full, call ZKVerifier.sol to check proof validity
        // For now, assume caller is verifier; add require(proofValid(_proofHash), "Invalid proof");

        verifiedProofs[_proofHash] = true;
        verificationRewards[_verifier] += verificationRewardAmount;

        _mint(_verifier, verificationRewardAmount);

        emit ProofVerified(_verifier, _proofHash, verificationRewardAmount);
    }

    function setVerificationReward(uint256 _newReward) external onlyOwner {
        verificationRewardAmount = _newReward;
    }

    // Admin Functions
    function setStakingRewardRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Rate <=10%");
        stakingRewardRate = _newRate;
    }

    function setMinimumStakePeriod(uint256 _newPeriod) external onlyOwner {
        minimumStakePeriod = _newPeriod;
    }

    function setDeflationBurnRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 100, "Burn rate <=1%");
        deflationBurnRate = _newRate;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function initiateEmergencyWithdraw(address _token, uint256 _amount) external onlyOwner {
        emergencyWithdrawTimestamp = block.timestamp + EMERGENCY_TIMELOCK;
    }

    function executeEmergencyWithdraw(address _token, uint256 _amount) external onlyOwner {
        require(block.timestamp >= emergencyWithdrawTimestamp, "Timelock not met");
        require(emergencyWithdrawTimestamp > 0, "No withdrawal initiated");

        emergencyWithdrawTimestamp = 0;

        if (_token == address(0)) {
            (bool success, ) = payable(owner()).call{value: _amount}("");
            require(success, "ETH withdrawal failed");
        } else {
            ERC20(_token).transfer(owner(), _amount);
        }
    }

    // View Functions
    function getStakeInfo(address _staker) external view returns (
        uint256 amount,
        uint256 startTime,
        uint256 pendingReward,
        bool active,
        uint8 degree
    ) {
        StakeInfo memory userStake = stakes[_staker];
        return (
            userStake.amount,
            userStake.startTime,
            calculateStakingReward(_staker),
            userStake.active,
            userStake.proximityDegree
        );
    }

    // Required overrides (deflation only on staking, not transfers)
    function _update(address from, address to, uint256 value) internal override(ERC20, ERC20Pausable) {
        super._update(from, to, value);
    }

    receive() external payable {}
}

