// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "./TruthForgeToken.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ValidationPool
 * @dev Instance contract for validating a single news item. Deployed by PoolFactory.
 * Features:
 * - Micro-pool for initial consensus (sentiment/subject; skip ZK if easily verifiable).
 * - Main pool: Stake/sign with proximity degree (ZK stub for claims).
 * - Weighted consensus: Trust score * stake * degree multiplier.
 * - Closure: Time-bound or early; distribute rewards/refunds, slash cheats.
 * - Integrates $VERIFY for staking/rewards.
 * - Stub for ZKVerifier (full in separate contract).
 * - Anti-abuse: Anomaly detection stub (e.g., vote spikes).
 */
contract ValidationPool is Ownable, ReentrancyGuard {
    VerifyToken public token;
    bytes32 public newsHash;
    uint256 public endTime;
    bool public closed = false;
    bool public easilyVerifiable = false; // Set by Factory micro-pool

    // Voting: Verify (true) or discount (false)
    mapping(address => bool) public votes; // Verifier -> vote
    mapping(address => uint256) public stakesInPool; // Verifier -> staked amount
    uint256 public totalVerifyStake = 0;
    uint256 public totalDiscountStake = 0;

    // Consensus results
    bool public finalConsensus; // True if verify wins
    uint256 public totalVotes = 0;

    event VoteCast(address indexed verifier, bool vote, uint256 stake, uint8 degree);
    event PoolClosed(bytes32 indexed newsHash, bool consensus, uint256 verifyStake, uint256 discountStake);

    constructor(VerifyToken _token, bytes32 _newsHash, uint256 _endTime) Ownable(msg.sender) {
        token = _token;
        newsHash = _newsHash;
        endTime = _endTime;
    }

    // Cast vote with stake and degree (ZK stub for proof)
    function castVote(bool _vote, uint256 _stakeAmount, uint8 _degree) external nonReentrant {
        require(!closed, "Pool closed");
        require(block.timestamp < endTime, "Pool ended");
        require(_stakeAmount > 0, "Stake must be positive");
        require(stakesInPool[msg.sender] == 0, "Already voted"); // One vote per verifier
        require(_degree >= 1 && _degree <= 3, "Invalid degree");
        // ZK stub: Require valid proof (integrate ZKVerifier.verifyProof(proof, _degree, newsHash))
        // For now: Assume valid

        token.transferFrom(msg.sender, address(this), _stakeAmount);
        stakesInPool[msg.sender] = _stakeAmount;
        votes[msg.sender] = _vote;
        totalVotes++;

        // Track participant for distribution
        if (!hasParticipated[msg.sender]) {
            participants.push(msg.sender);
            hasParticipated[msg.sender] = true;
        }

        if (_vote) {
            totalVerifyStake += _stakeAmount;
        } else {
            totalDiscountStake += _stakeAmount;
        }

        emit VoteCast(msg.sender, _vote, _stakeAmount, _degree);
    }

    // Close and distribute (callable by owner or timelock; auto if easy)
    function closeAndDistribute() external onlyOwner {
        require(block.timestamp >= endTime || easilyVerifiable, "Not closable");
        _closeAndDistributeInternal();
    }

    // Track participants for distribution
    address[] public participants;
    mapping(address => bool) public hasParticipated;

    // Internal distribution (rewards to winners, slash losers 1%)
    function _distributeRewards(bool winningSide) internal {
        uint256 slashRate = 100; // 1% slash on losers
        uint256 winningStake = winningSide ? totalVerifyStake : totalDiscountStake;
        uint256 totalSlashed = 0;

        // Calculate total slashed amount first
        for (uint256 i = 0; i < participants.length; i++) {
            address verifier = participants[i];
            if (stakesInPool[verifier] > 0 && votes[verifier] != winningSide) {
                uint256 slashAmount = (stakesInPool[verifier] * slashRate) / 10000;
                totalSlashed += slashAmount;
            }
        }

        // Distribute rewards and refunds
        for (uint256 i = 0; i < participants.length; i++) {
            address verifier = participants[i];
            if (stakesInPool[verifier] > 0) {
                bool userVote = votes[verifier];
                uint256 userStake = stakesInPool[verifier];

                if (userVote == winningSide) {
                    // Reward: Original stake + proportional share of slashed tokens
                    uint256 rewardShare = (userStake * totalSlashed) / winningStake;
                    token.transfer(verifier, userStake + rewardShare);
                } else {
                    // Return stake minus slash
                    uint256 slashAmount = (userStake * slashRate) / 10000;
                    token.transfer(verifier, userStake - slashAmount);
                    // Slashed amount stays in contract for winner distribution
                }

                stakesInPool[verifier] = 0;
            }
        }
    }

    // Set easy flag (from Factory micro-pool)
    function setEasilyVerifiable(bool _flag) external onlyOwner {
        easilyVerifiable = _flag;
        if (_flag) {
            _closeAndDistributeInternal(); // Auto-close if easy
        }
    }

    // Internal version that doesn't need onlyOwner check
    function _closeAndDistributeInternal() internal {
        require(!closed, "Already closed");

        closed = true;

        // Determine winner (weighted by stake; add trust/degree in full)
        if (totalVerifyStake > totalDiscountStake) {
            finalConsensus = true;
            // Reward verify side
            _distributeRewards(true);
        } else {
            finalConsensus = false;
            // Reward discount side
            _distributeRewards(false);
        }

        emit PoolClosed(newsHash, finalConsensus, totalVerifyStake, totalDiscountStake);
    }

    // View: Get pool status
    function getPoolStatus() external view returns (
        bool isClosed,
        bool isVerifiable,
        uint256 verifyStake,
        uint256 discountStake,
        uint256 remainingTime
    ) {
        uint256 timeLeft = (block.timestamp < endTime) ? endTime - block.timestamp : 0;
        return (closed, easilyVerifiable, totalVerifyStake, totalDiscountStake, timeLeft);
    }

    // Admin: Update durations/thresholds (from Factory)
    function updateEndTime(uint256 _newEndTime) external onlyOwner {
        endTime = _newEndTime;
    }
}

